userDefinedLinkHintCss:
div > .vimiumHintMarker {
background: #1d0c13;
border: 1px solid #1d0c13;
}

div > .vimiumHintMarker span {
color: #e76680;
font-weight: normal;
font-size: 12px;
text-shadow: 0 1px 0 rgba(255, 255, 255, 0.2) !important;
}

div > .vimiumHintMarker > .matchingCharacter {
}

/* HUD */
#hud-container {
background: #1d0c13;
border: 1px solid #e76680;
color: #e76680 !important;
font-weight: bold;
font-size: 12px;
}

#search-area {
  color: #e76680;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 14px;
  height: 30px;
  margin-bottom: 0;
  padding: 2px 4px;
  border-radius: 3px;
  width: 100%;
  outline: none;
  box-sizing: border-box;
  line-height: 20px;
}
